graphql.query:
query($filter: FilterEmployee) {
    employees(filter: $filter, sort: [["employeeNumber", "DESC"]], limit: 1) {
        employeeNumber
        lastName
        firstName
        customers {
            customerName
            customerNumber
        }
    }
}

query($filter: FilterCustomer) {
    customers(filter: $filter, limit: 1) {
        customerNumber
        customerName
        employee {
            employeeNumber
            lastName
            firstName
        }
        payments {
            paymentDate
            amount
        },
        orders {
            orderNumber
            orderDate
            requiredDate
            shippedDate
            status
            orderdetails {
                productCode
                quantityOrdered
                priceEach
                orderLineNumber
                products {
                    productCode
                    productName
                    productLine
                    productScale
                    productVendor
                    productDescription
                    quantityInStock
                    buyPrice
                    MSRP
                }
            }
        }
    }
}

query {
    payments(limit: 1) {
        checkNumber
        paymentDate
        amount
        customer {
            customerNumber
            customerName
        }
    }
}

query($filter: FilterOrder) {
    orders(filter: $filter, limit: 1) {
        orderNumber
        orderDate
        requiredDate
        shippedDate
        status
        customer {
            customerName
            customerNumber
        }
        orderdetails {
            productCode
            quantityOrdered
            priceEach
            orderLineNumber
        }
    }
}

query($filter: FilterOrder) {
    orderdetails(filter: $filter, limit: 10, offset: 0) {
        orderNumber
        productCode
        quantityOrdered
        priceEach
        orderLineNumber
        products {
            productCode
            productName
            productLine
            productScale
            productVendor
            productDescription
            quantityInStock
            buyPrice
            MSRP
        }
        order {
            orderNumber
            orderDate
            requiredDate
            shippedDate
            status
            customer {
                customerName
                customerNumber
            }
        }
    }
}

query($filter: FilterProduct) {
    products(filter: $filter, sort: [["productName", "ASC"]], limit: 2) {
        productCode
        productName
        productLine
        productScale
        productVendor
        productDescription
        quantityInStock
        buyPrice
        MSRP
        category {
            productLine
            textDescription
        }
    }
}

query($filter: FilterProductLine) {
    productlines(filter: $filter, sort: [["productLine", "ASC"]], limit: 2) {
        productLine
        textDescription
    }
}
